var notify;

$(document).ready(function () {

    notify = function (text, type = "success", hideDelay = 5000) {
        var newWrap = $(wrap).clone().addClass(type);

        $(newWrap).appendTo($("#notify-container"));
        $(newWrap).slideToggle(200);
        $("span", newWrap).html(text);

        setTimeout(function () {
            $(newWrap).slideToggle(600);
            setTimeout(function () {
                $(newWrap).remove();
            }, 1000);
        }, hideDelay);
    }

    $('body').append("<div id='notify-container'><div class='notify-wrapper'><span class='notify-data'></span></div></div>");
    var wrap = $(".notify-wrapper");
});